// Get all labels from the "Get many labels" node
const labels = $items("Get many labels").map(item => item.json);

// Get the user's chat message from "When chat message received"
const chatItem = $items("When chat message received")[0].json;

// Use the actual property from the trigger: chatInput
const userText =
  chatItem.chatInput ||          // <- this is the one your trigger uses
  chatItem.text ||
  chatItem.message ||
  chatItem.query ||
  "";

// Build a single object to send to OpenAI
return [
  {
    json: {
      user_text: userText,
      labels: labels,
    },
  },
];

