// The agent output is inside $json.output, but wrapped in ```json ... ```
let raw = $json.output || "";

// Remove fences like ```json ... ```
raw = raw.replace(/```json/gi, "").replace(/```/g, "").trim();

// Parse the JSON inside
let parsed;

try {
  parsed = JSON.parse(raw);
} catch (e) {
  // Fall back to an empty object if parsing fails
  parsed = { parseError: true, raw };
}

// If the parsed value is an array (which it is for tool calls),
// take the first element so `json` is a plain object
const itemJson = Array.isArray(parsed) ? (parsed[0] || {}) : parsed;

// ‚úÖ Return in n8n's expected format
return [
  {
    json: itemJson,
  },
];
